// source secrets.sh

import example from './connect.example.json'
import connect from './connect.js'

const { event, context } = example

connect
	.handler(event, context)
	.then(output => {
		console.log('DONE', JSON.stringify(output, undefined, 2))
		process.exit(0)
	})
	.then(error => {
		console.log('ERROR', JSON.stringify(error, undefined, 2))
		process.exit(1)
	})
