export class NotFoundException extends Error {
	constructor(message, meta) {
		super(message)
		this.code = 'NotFoundException'
		this.title = 'Not Found'
		this.status = '404'
		this.detail = message || 'Route was valid but resource was not found.'

		if (meta || typeof message === 'object') {
			this.meta = meta
		}
	}
}
