export class UnauthorizedAccessException extends Error {
	constructor(message, meta) {
		super(message)
		this.code = 'UnauthorizedAccessException'
		this.title = 'Unauthorized Access'
		this.status = '404'
		this.detail = message || 'Requesting user does not have necessary authorization.'

		if (meta || typeof message === 'object') {
			this.meta = meta
		}
	}
}
