export const formatError = error => {
	const formatted = {
		status: error.status || '500',
		code: error.code || 'UnexpectedException',
		title: error.title || error.name || 'Unexpected Server Error',
		detail: error.detail || error.message || 'The server threw an error which is not properly formatted.'
	}
	if (error.id) {
		formatted.id = error.id
	}
	if (error.links) {
		formatted.links = error.links
	}
	if (error.source) {
		formatted.source = error.source
	}
	if (error.meta && typeof error.meta === 'object') {
		formatted.meta = error.meta
	}
	return formatted
}
