const textEnc = new TextEncoder('utf-8')
const encode = textEnc.encode.bind(textEnc)

export async function hash(message) {
	return crypto
		.subtle
		.digest('SHA-256', encode(message))
		.then(buffer => Array
			.prototype
			.map
			.call(
				new Uint8Array(buffer),
				x => x.toString(16).padStart(2, '0')
			)
			.join('')
		)
}
