export const omit = (obj, keys) => {
	const shallowCopy = Object.assign({}, obj)
	keys.forEach(key => {
		delete shallowCopy[key]
	})
	return shallowCopy
}

export const partition = (list, splitter) => list.reduce(([ matches, remainder ], item) => {
	splitter(item)
		? matches.push(item)
		: remainder.push(item)
	return [ matches, remainder ]
}, [ [], [] ])
