import snarkdown from 'snarkdown'

const page = `
# BraidedJS

This is the front page.

There is a lot going on here, I can assure you!
`

const template = content => `<!DOCTYPE html>
<html lang="en-US">
<head>
	<meta charset="UTF-8">
	<title>BraidedJS</title>
</head>
<body>
	${content}
</body>
</html>
`

export const handleRequest = async request => {
	return new Response(template(snarkdown(page)), {
		headers: {
			'Content-Type': 'text/html'
		},
		status: 200
	})
}
